import React from "react";
import Loader from "react-loader-spinner";
import QRCode from "qrcode";
import { useEffect, useState } from "react";
const QRSection = ({ text }) => {
  const [src, setSrc] = useState("");
  useEffect(() => {
    QRCode.toDataURL("Hi Ameg").then((data) => {
      setSrc(data);
    });
  }, []);
  return (
    <div className="qrcode-section m-8 flex flex-col items-center w-full  h-full  rounded">
      <div className="container h-16">
        <Loader type="TailSpin" color="#575ce5" height={60} width={60} />
        <img src={src} className="" alt="QR" />
      </div>
    </div>
  );
};
export default QRSection;
